mapper.select:
select 'false' as QUERYID,

from t_apply
where apply_id = #{applyId,jdbcType=INTEGER}
  and activity_id = #{activityId,jdbcType=INTEGER}

mapper.delete:
delete from t_apply
where apply_id = #{applyId,jdbcType=INTEGER}
  and activity_id = #{activityId,jdbcType=INTEGER}

mapper.update:
update t_apply
set applicant_name = #{applicantName,jdbcType=VARCHAR},
  applicant_tele = #{applicantTele,jdbcType=VARCHAR},
  applicant_reason = #{applicantReason,jdbcType=VARCHAR},
  apply_state = #{applyState,jdbcType=VARCHAR},
  feedback = #{feedback,jdbcType=VARCHAR}
where apply_id = #{applyId,jdbcType=INTEGER}
  and activity_id = #{activityId,jdbcType=INTEGER}
