mapper.select:
select 'false' as QUERYID,

from t_donate
where donate_id = #{donateId,jdbcType=INTEGER}

mapper.update:
update t_donate
set benefactor_name = #{benefactorName,jdbcType=VARCHAR},
  benefactor_tele = #{benefactorTele,jdbcType=VARCHAR},
  donate_time = #{donateTime,jdbcType=TIMESTAMP},
  donate_describe = #{donateDescribe,jdbcType=VARCHAR}
where donate_id = #{donateId,jdbcType=INTEGER}
