mapper.select:
select 'false' as QUERYID,

from t_share
where share_id = #{shareId,jdbcType=INTEGER}

mapper.update:
update t_share
set adopter_id = #{adopterId,jdbcType=INTEGER},
  share_time = #{shareTime,jdbcType=TIMESTAMP},
  share_type = #{shareType,jdbcType=VARCHAR},
  share_content = #{shareContent,jdbcType=VARCHAR}
where share_id = #{shareId,jdbcType=INTEGER}
